const express=require("express"),router=express.Router(),Articulos=require("../../models/Articulos");router.get("/",async(req,res,next)=>{try{const name=req.query.name,filtro={};name&&(filtro.name=name);const articulos=await Articulos.lista(filtro);res.json({results:articulos})}catch(err){next(err)}}),router.post("/",async(req,res,next)=>{try{const artData=req.body,articulo=new Articulos(artData),artSave=await articulo.save();res.json({result:artSave})}catch(err){next(err)}}),module.exports=router;